#!/bin/bash

alter_readme() {
    cat > README.md <<EOF
# Cosmic Money for Android

Shared budget manager able to sync with [I Love Money](https://github.com/BoboTiG/ilovemoney).

**Note**: This is a modified version of [Money Buster](https://gitlab.com/eneiluj/moneybuster) only suitable for my needs:
- new project title: Cosmic Money
- new logo (https://www.svgrepo.com/svg/493510/money-tree)
- set "Show Nextcloud settings" false by default
EOF
}

more_ignored_files() {
    if ! /bin/grep -o 'app/dev' .gitignore >/dev/null; then
        echo -e 'app/dev/\napp/normal/' >> .gitignore
    fi
}

rename_project() {
    # Folders
    /bin/find app -type d -name eneiluj -print0 | /bin/sed -ze 'p;s/eneiluj/bobotig/' | /bin/xargs -0 -n2 mv 2>/dev/null || true
    /bin/find app -type d -name moneybuster -print0 | /bin/sed -ze 'p;s/moneybuster/cosmicmoney/' | /bin/xargs -0 -n2 mv 2>/dev/null || true

    # File names
    /bin/find . -type f -name '*moneybuster*' -print0 | /bin/sed -ze 'p ; s/moneybuster/cosmicmoney/' | /bin/xargs -0 -n2 mv 2>/dev/null || true
    /bin/find app -type f -name 'MoneyBuster*' -print0 | /bin/sed -ze 'p ; s/MoneyBuster/CosmicMoney/' | /bin/xargs -0 -n2 mv 2>/dev/null || true

    # File content
    /bin/find app \( -type f -name 'strings.xml' -prune \) -o -type f -print0 | \
        /bin/xargs -0 /bin/sed -i 's/ihate/ilove/g ; s/iHate/ILove/g ; s/IHate/ILove/g ; s/IHATE/ILOVE/g ; s/moneybuster/cosmicmoney/g ; s/moneyBuster/cosmicMoney/g ; s/MoneyBuster/CosmicMoney/g ; s/Money Buster/Cosmic Money/g ; s/Moneybuster/Cosmicmoney/g ; s/MoneyDevBuster/Cosmic Money NG/g ; s/\.eneiluj/.bobotig/g'
    /bin/find app/src/main/res -type f -name 'strings.xml' -print0 | \
        /bin/xargs -0 /bin/sed -i 's/MoneyBuster/Cosmic Money/g ; s/ihate/ilove/g ; s/IHate/ILove/g ; s#<string name="about_maintainer".*#<string name="about_maintainer">Mickaël Schoentgen • \&lt;a href="https://github.com/BoboTiG/cosmic-money">Code Source\&lt;/a></string># ; s#Cospend/##g'
    /bin/find app -type f -name 'build.gradle' -print0 | \
        /bin/xargs -0 /bin/sed -i 's/CosmicMoney/Cosmic Money/'
}

